mapper.insert:

	insert into category 
	values(
		null,
		#{name},
		#{depth},
		#{parentsNo},



		(select ifnull(max(c.group_no)+1, 1) from category c)
	);




		#{groupNo}
	);

mapper.update:
		
			update category
		
		
		    
    	     
		        name = #{vo.name},
		     
		    
		    
		     
			    parents_no = #{vo.parentsNo},
		     
		    
			
		     
			    group_no = #{vo.groupNo}
		     
		    
		
		
			where no = #{no}
