mapper.select:
select no, name, price, reg_date as regDate, exhibition_availability as exhibitionAvailability, option_availability as optionAvailability, sails_status as sailsStatus, exhibition_rank as exhibitionRank, count, detail, shipping_fee as shippingFee, category_no as categoryNo from product
where name like CONCAT('%', #{keyword}, '%')
where category_no = #{keyword}
